import "jsr:@supabase/functions-js/edge-runtime.d.ts";

const corsHeaders = {
  'Access-Control-Allow-Origin': '*',
  'Access-Control-Allow-Headers': 'authorization, x-client-info, apikey, content-type',
};

Deno.serve(async (req: Request) => {
  // Handle CORS preflight requests
  if (req.method === 'OPTIONS') {
    return new Response('ok', { headers: corsHeaders });
  }

  try {
    const { dealDetails, scores } = await req.json();

    const openaiApiKey = Deno.env.get('OPENAI_API_KEY');
    if (!openaiApiKey) {
      throw new Error('OPENAI_API_KEY is not set');
    }

    // Construct the prompt for OpenAI
    const prompt = `You are an expert sales consultant analyzing a Right-to-Win (RTW) assessment based on the CEO-Led Sales 4C model.

Deal Details:
- Client: ${dealDetails.clientName}
- Deal: ${dealDetails.dealName}
- Value: ${dealDetails.dealValue || 'Not specified'}
- Expected Close: ${dealDetails.expectedCloseDate || 'Not specified'}
- Sales Stage: ${dealDetails.salesStage || 'Not specified'}
- Context: ${dealDetails.dealContext || 'Not specified'}

Assessment Scores (0-100):
- Credibility: ${scores.credibility}%
- Capability: ${scores.capability}%
- Commitment: ${scores.commitment}%
- Control: ${scores.control}%
- Overall RTW Score: ${scores.rtw}%

Provide a concise analysis (150-200 words) covering:
1. Key strengths and weaknesses across the 4Cs
2. The most critical area requiring attention
3. 3-4 specific, actionable next steps to improve the RTW score

Use British English and avoid buzzwords. Be direct and practical.`;

    // Call OpenAI API
    const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${openaiApiKey}`,
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({
        model: 'gpt-4o',
        messages: [
          {
            role: 'system',
            content: 'You are an expert sales consultant specializing in CEO-Led Sales methodology and Right-to-Win assessments. Provide clear, actionable insights in British English.'
          },
          {
            role: 'user',
            content: prompt
          }
        ],
        temperature: 0.7,
        max_tokens: 500,
      }),
    });

    if (!openaiResponse.ok) {
      const error = await openaiResponse.text();
      throw new Error(`OpenAI API error: ${error}`);
    }

    const data = await openaiResponse.json();
    const insights = data.choices[0].message.content;

    return new Response(
      JSON.stringify({ insights }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 200,
      }
    );
  } catch (error) {
    console.error('Error:', error);
    return new Response(
      JSON.stringify({ error: error.message }),
      {
        headers: { ...corsHeaders, 'Content-Type': 'application/json' },
        status: 400,
      }
    );
  }
});

