          headers: { ...corsHeaders, 'Content-Type': 'application/json' },
          status: 500,
        }
      );
    }

    // Build natural language responses section
    let naturalLanguageSection = '';
    if (answers) {
      const responses = [];

      if (answers.credibility?.knowledgeResponse) {
        responses.push(`Knowledge: "${answers.credibility.knowledgeResponse}"`);
      }
      if (answers.credibility?.trustResponse) {
        responses.push(`Trust: "${answers.credibility.trustResponse}"`);
