        responses.push(`Satisfaction: "${answers.commitment.satisfactionResponse}"`);
      }
      if (answers.control?.masteryResponse) {
        responses.push(`Mastery: "${answers.control.masteryResponse}"`);
      }
      if (answers.control?.influenceResponse) {
        responses.push(`Influence: "${answers.control.influenceResponse}"`);
      }

      if (responses.length > 0) {
        naturalLanguageSection = `\n\nNATURAL LANGUAGE RESPONSES FROM ASSESSOR:\n${responses.join('\n')}`;
      }
    }

    // Construct a detailed prompt for OpenAI based on ImpactWon methodology
    const prompt = `You are an expert sales consultant specializing in the ImpactWon Right-to-Win (RTW) methodology based on CEO-Led Sales 4C model.

